import React, {Component} from 'react';
import {Col} from 'react-bootstrap';
import {connect} from 'react-redux';
import ListGroup from 'react-bootstrap/ListGroup'
import ReactPaginate from 'react-paginate';
import { updatePage } from "../../redux/actions";

const mapStateToProps = (state) => {
	return {
		collection: state.collection,
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		updatePage: (pageUpdate) => dispatch(updatePage(pageUpdate)),
	};
};

class AppList extends Component {
	handlePageClick = (e) => {
		const selectedPage = e.selected;
		const offset = selectedPage * this.state.perPage;

		this.props.updatePage({
			currentPage: selectedPage,
			currentIndex: offset,
		}, () => {
			this.props.doSearch();
		});
	}

	render() {
		const collection = this.props.collection
			&& this.props.collection[this.props.collectionToUse];
		const collectionToMap = collection.books;
		const pageCount = collection.pageCount;

		return (
			<Col>
				<ListGroup>
					{collection.map((singleBook, index) => (
						<AppBook key={index} user={singleBook}/>
					))}
				</ListGroup>
				<ReactPaginate
					previousLabel={'prev'}
					nextLabel={'next'}
					breakLabel={'...'}
					breakClassName={'break-me'}
					pageCount={this.props.pageCount}
					marginPagesDisplayed={2}
					pageRangeDisplayed={5}
					onPageChange={this.handlePageClick}
					containerClassName={'pagination'}
					subContainerClassName={'pages pagination'}
					activeClassName={'active'}/>
			</Col>
		);
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(AppList);
