import {Subject} from 'rxjs'

const trackFavorites = new Subject();

const favoriteService = {
	deleteFavorite: favoriteId => trackFavorites.next(favoriteId),
	updateFavorites: () => trackFavorites.asObservable()
}

export default favoriteService;
