import React, {Component} from 'react';
import {Row, Col, ListGroup} from 'react-bootstrap'
import {connect} from "react-redux";
import {addFavorite, deleteFavorite} from "../../redux/actions";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { truncate } from "../../helpers/misc.helpers";
import './book.css';

const mapStateToProps = (state) => {
	return {
		favorites: state.list,
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		addFavorite: (favorite) => dispatch(addFavorite(favorite)),
		deleteFavorite: (favorite) => dispatch(deleteFavorite(favorite)),
	};
};

class AppBook extends Component {
	addFavorite() {

	}

	deleteFavorite() {

	}

	render() {
		const singleBook = this.props.singleBook;
		const emptyHeart = <FontAwesomeIcon size="2x" icon={["far", "heart"]} />
		const fullHeart = <FontAwesomeIcon size="2x" icon={["fas", "heart"]} />
		const isFavorite = this.props.favorites && this.props.favorites.includes(singleBook.id);
		return (
			<ListGroup.Item>
				<Row>
					<Col xs={2}><a rel="noopener noreferrer" target={"_blank"} href={singleBook.link}><img src={singleBook.image} alt={singleBook.title}/></a></Col>
					<Col xs={10}>
						<Row>
							<Col>
								<span className="book-label-title mr-2"><a rel="noopener noreferrer" target={"_blank"} href={singleBook.link}>{singleBook.title}</a></span>
							</Col>
							<Col className="position-relative">
								<span className="position-absolute corner-heart">
									{isFavorite
										? <span className="pointer" title="favorite" onClick={(e) => this.deleteFavorite(singleBook.id)}>{fullHeart}</span>
										: <span className="pointer" title="favorite" onClick={(e) => this.addFavorite(singleBook.id)}>{emptyHeart}</span>}
								</span>
							</Col>
						</Row>
						<Row><Col><span className="font-weight-bold mr-1">By:</span> {singleBook.authors.join(", ")}</Col></Row>
						<Row className="mt-3"><Col><span className="font-weight-bold mr-1">Description:</span>{truncate(singleBook.description, 1000, true)}</Col></Row>
						<Row className="mt-2"><Col><span className="font-weight-bold">Categories: </span>{singleBook.categories.join(", ")}</Col></Row>
						<Row className="mt-2"><Col><span className="font-weight-bold">Pages: </span>{singleBook.pageCount}</Col><Col><span className="font-weight-bold">Average Rating: </span>{singleBook.averageRating ? singleBook.averageRating : 'N/A'}</Col></Row>
					</Col>
				</Row>
			</ListGroup.Item>
		);
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(AppBook);
