import axios from 'axios';

class FavoriteApi {
    url = '/api/books';

    async saveFavorite(favorite) {
    	return axios.post(url, {
    		favorite: JSON.stringify(favorite)
    	});
    }

    async deleteFavorite() {
    	return axios.delete();
    }

    async getFavorites() {
    	return axios.get();
    }
}

export default FavoriteApi;
