import React, {Component} from 'react';
import {Row, Col, ListGroup} from 'react-bootstrap'
import {connect} from "react-redux";
import {addFavorite, deleteFavorite} from "../../redux/actions";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import './book.css';

const mapStateToProps = (state) => {
	return {
		collection: state.collection,
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		addFavorite: (favorite) => dispatch(addFavorite(favorite)),
		deleteFavorite: (favorite) => dispatch(deleteFavorite(favorite)),
	};
};

class AppBook extends Component {
	componentDidMount() {

	}

	render() {
		const singleBook = this.props.singleBook;
		const emptyHeart = <FontAwesomeIcon size="4x" icon={["far", "faHeart"]} />
		const fullHeart = <FontAwesomeIcon size="4x" icon={["fas", "faHeart"]} />
		const isFavorite = false;

		return (
			<ListGroup.Item>
				<Row>
					<Col xs={2}><img src={singleBook.image} alt={singleBook.title}/></Col>
					<Col xs={10}>
						<Row>
							<Col>
								<span className="book-label-title mr-2">Title:</span>
								<span className="book-title">{singleBook.title}</span>
							</Col>
							<Col className="corner-heart">
								<span className="mr-2">
									{isFavorite
										? <span className="pointer" onClick={(e) => this.deleteFavorite(singleBook.id)}>fullHeart</span>
										: <span className="pointer" onClick={(e) => this.addFavorite(singleBook.id)}>emptyHeart</span>}
								</span>
							</Col>
						</Row>
						<Row><Col></Col></Row>
						<Row><Col>Description</Col></Row>
						<Row><Col>PageCount</Col><Col>AveragRating</Col></Row>
						<Row><Col>Categories</Col></Row>
						<Row><Col>Link</Col></Row>
					</Col>
				</Row>
			</ListGroup.Item>
		);
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(AppBook);
