import React, {Component} from 'react';
import {Row, Col, ListGroup} from 'react-bootstrap'
import {connect} from "react-redux";
import {addFavorite, deleteFavorite} from "../../redux/actions";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import './book.css';

const mapStateToProps = (state) => {
	return {
		collection: state.collection,
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		addFavorite: (favorite) => dispatch(addFavorite(favorite)),
		deleteFavorite: (favorite) => dispatch(deleteFavorite(favorite)),
	};
};

class AppBook extends Component {
	componentDidMount() {

	}

	render() {
		const singleBook = this.props.singleBook;
		const emptyHeart = <FontAwesomeIcon size="2x" icon={["far", "heart"]} />
		const fullHeart = <FontAwesomeIcon size="2x" icon={["fas", "heart"]} />
		const isFavorite = false;
		return (
			<ListGroup.Item>
				<Row>
					<Col xs={2}><a href={singleBook.link}><img src={singleBook.image} alt={singleBook.title}/></a></Col>
					<Col xs={10}>
						<Row>
							<Col>
								<span className="book-label-title mr-2"><a href={singleBook.link}>{singleBook.title}</a></span>
							</Col>
							<Col className="position-relative">
								<span className="position-absolute corner-heart">
									{isFavorite
										? <span className="pointer" title="favorite" onClick={(e) => this.deleteFavorite(singleBook.id)}>{fullHeart}</span>
										: <span className="pointer" title="favorite" onClick={(e) => this.addFavorite(singleBook.id)}>{emptyHeart}</span>}
								</span>
							</Col>
						</Row>
						<Row className="mt-3"><Col><span className="">Description:</span>{singleBook.description}</Col></Row>
						<Row><Col><span className="bold">Pages: </span>{singleBook.pageCount}</Col><Col>AveragRating</Col></Row>
						<Row><Col>Categories</Col></Row>
						<Row><Col>Link</Col></Row>
					</Col>
				</Row>
			</ListGroup.Item>
		);
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(AppBook);
