import {ADD_FAVORITE, DELETE_FAVORITE, UPDATE_BOOKS, UPDATE_PAGE} from '../constants/action-types';

const initialState = {
	collection: [],
	favorites: [],
};

const rootReducer = (state = initialState, action) => {
	if (action.type === UPDATE_BOOKS) {
		// get the correct collection
		const collectionToUpdate = action.payload.collectionName;
		if (!collectionToUpdate) {
			return;
		}
		const currentCollection = state.collection[collectionToUpdate];
		if (!currentCollection) {
			return;
		}
		return Object.assign({}, currentCollection, {
			books: action.payload.books,
			pageCount: action.payload.pageCount,
			currentPage: action.payload.currentPage,
			currentIndex: action.payload.currentIndex,
		});
	}

	if (action.type === UPDATE_PAGE) {
		const collectionToUpdate = action.payload.collectionName;
		if (!collectionToUpdate) {
			return;
		}
		const currentCollection = state.collection[collectionToUpdate];
		if (!currentCollection) {
			return;
		}
		return Object.assign({}, currentCollection, {
			currentIndex: action.payload.currentIndex,
			currentPage: action.payload.currentPage,
		});
	}

	if (action.type === ADD_FAVORITE) {
		return Object.assign({}, state, {
			favorites: state.favorites.concat(action.payload),
		});
	}

	if (action.type === DELETE_FAVORITE) {
		return Object.assign({}, state, {
			favorites: state.favorites.concat(action.payload),
		});
	}
	return state;
};
export default rootReducer;
