import {Observable, Subject} from 'rxjs'

class PaginationService {
     updateSearch = new Subject();
     updateSearchChange$ = this.updateSearch.asObservable();

     constructor() { }

     pageClicked(pageNumber){
     	this.updateSearch.next(pageNumber);
     }
}

export default PaginationService;
